# !/bin/bash

# Creates and returns a random word

# How it works
programname=$0

function usage {
    echo "usage: $programname [-m minsyllables] [-M maxsyllabs]"
    echo "  -n minsyllables   specify minimum number of syllabes"
    echo "  -M maxsyllables   specify maximum number of syllabes"
    exit 1
}

# Print errors
die() {
  printf '%s\n' "$1" >&2
  exit 1
}

MINSYLLABS=2
MAXSYLLABS=3
NUMSYLLABLES=0
WORD=""

# Check arguments
while :; do
  case $1 in
    -m)
      if [[ $2 =~ ^-?[0-9]+$ ]]; then
        MINSYLLABS=$2
        shift
      else
        die "ERROR: '-m' requires a non-empty integer option argument."
      fi
      ;;
    -M)
      if [[ $2 =~ ^-?[0-9]+$ ]]; then
        MAXSYLLABS=$2
        shift
      else
        die "ERROR: '-M' requires a non-empty integer option argument."
      fi
      ;;
    *)               # Default case: No more options, so break out of the loop.
      if [[ $1 =~ ^-?[0-9]+$ ]]; then
        MINSYLLABS=$1
        MAXSYLLABS=$1
        NUMSYLLABLES=$1
        shift
      fi
      break
  esac

  shift
done

if [[ $MINSYLLABS > $MAXSYLLABS ]]; then
  MAXSYLLABS=$(($MINSYLLABS+$MAXSYLLABS))
fi


