# echo "Generating a word of $NUMSYLLABLES syllables"

# Generate word
COUNTER=0
while [[ $COUNTER -lt $NUMSYLLABLES ]]; do
  # Seed random generator
  RANDOM=$$$(date +%s)
  SYLLABLEINDEX=$(($RANDOM % ${#SYLLABLES[@]}))
  # Get random expression...
  SYLLABLE=${SYLLABLES[$SYLLABLEINDEX]}
  WORD="$WORD$SYLLABLE"
  let COUNTER=COUNTER+1
  sleep 1
done

echo $WORD
